package pemx

import (
	"github.com/louismax/cryptoX/rsax"
	"testing"
)

var (
	privatePKCS8 = ``
	pubKeyPKCS8  = ``
)

func TestDecodePrivateKey(t *testing.T) {
	s := rsax.FormatPrivateKey(privatePKCS8)

	p, err := DecodePrivateKey([]byte(s))
	if err != nil {
		t.Error(err)
	}
	t.Log(p.Validate())
}
func TestDecodePublicKey(t *testing.T) {
	s := rsax.FormatPublicKey(pubKeyPKCS8)

	p, err := DecodePublicKey([]byte(s))
	if err != nil {
		t.Error(err)
	}

	t.Log(p.Size())
}
